// Java Program to demonstrate Arrays.parallelSort() method

import java.util.Arrays;

public class Methods_In_Java_Array_Example12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int intAry[] = { 10, 20, 15, 22, 35 };
		
		Arrays.parallelSort(intAry);
		System.out.println("Integer Array: " + Arrays.toString(intAry));
	}
}
