
public class ShiftOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0x0005;
		int b = -10;
		
		System.out.println("a << 2 = " + (a << 2));
		System.out.println("a >> 2 = " + (a >> 2));
		
		// unsigned right shift operator 
		System.out.println("b >>> 2 = " + (b >>> 2));
	}
}
